    useEffect(()=>{
        axios
        .get('https://restcountries.com/v3.1/all')
        .then((res) => setData(res.data.flatMap(element => 
        {return [element.common,element.population,element.capital,element.flag]})));
        console.log(data)
    },[]);


    return (
        <div>
        <p>{data}</p>
        <button >Say Aurevoirt</button>

        </div>
    );
};

export default Countries;
